package com.example.customer_list;

import com.example.customer_list.model.Customer;

import java.io.*;
import java.util.ArrayList;
import java.util.List;
import javax.servlet.ServletException;
import javax.servlet.http.*;
import javax.servlet.annotation.*;

@WebServlet(name = "customerServlet", value = "/customer")
public class CustomerServlet extends HttpServlet {
    private static List<Customer> customerList = new ArrayList<>();
    static {
        customerList.add(new Customer("Nguyễn A", "1980-08-18", "Hà Nội", true,"data:image/jpeg;base64,"));
        customerList.add(new Customer("Ngô B", "1990-09-19", "Đà Nẵng", false, "data:image/jpeg;base64,"));
        customerList.add(new Customer("Trần C", "2000-10-20", "Tp Hồ Chí Minh", true, "data:image/jpeg;base64,"));
        customerList.add(new Customer("Lê D", "1985-05-15", "Hà Nội", false,"data:image/jpeg;base64,"));
        customerList.add(new Customer("Khúc E", "1983-08-20", "Đà Nẵng", true, "data:image/jpeg;base64,"));
    }

    public void init() {
    }

    public void doGet(HttpServletRequest request, HttpServletResponse response) throws IOException, ServletException {
        request.setAttribute("customerList", customerList);
        request.getRequestDispatcher("customer.jsp").forward(request,response);
    }

    public void doPost(HttpServletRequest request, HttpServletResponse response) throws IOException, ServletException {
        request.setAttribute("customerList", customerList);
        request.getRequestDispatcher("customer.jsp").forward(request,response);
    }

    public void destroy() {
    }
}